options.elementValueChanged:
if (context.element.id === 'plant_pick') {
  const v = String(context.element.value ?? '').trim();
  context.grafana.locationService.partial({'var-edit_plant_id': v}, true);
  const syncObj = {};
  syncObj['plant_pick'] = v;
  context.panel.setFormValue(syncObj);
  if (!v) {
    context.panel.setFormValue({ plant_id: '', plant_name: '', longitude: '', latitude: '', timezone: 'Asia/Shanghai' });
  }
  const focusPrimaryField = () => {
    if (typeof document === 'undefined') return;
    const selectors = [
      'input[name="plant_id"]',
      'textarea[name="plant_id"]',
      'input[id="plant_id"]',
      '[aria-label="plant_id"]',
      '[data-testid*="plant_id"] input',
    ];
    for (const selector of selectors) {
      const el = document.querySelector(selector);
      if (el && typeof el.focus === 'function') {
        el.focus();
        if (typeof el.select === 'function') {
          el.select();
        }
        break;
      }
    }
  };
  setTimeout(focusPrimaryField, 0);
  setTimeout(focusPrimaryField, 120);
}

