const autoId = String('${row_delete_plant_id}' ?? '').trim();
if (!autoId) return;
const guardStore = (typeof window !== 'undefined')
  ? (window.__iotAdminDeleteGuards = window.__iotAdminDeleteGuards || {})
  : null;
const guardKey = 'row_delete_plant_id:' + autoId;
if (guardStore && guardStore[guardKey]) return;
if (guardStore) {
  guardStore[guardKey] = true;
}
const releaseGuard = () => {
  if (guardStore) {
    delete guardStore[guardKey];
  }
};
return (async () => {
  try {
    if (!confirm('Á°ÆËÆ§Âà†Èô§ÂéÇÁ´ô ' + autoId + 'ÔºàÈùûÂº∫Âà∂Âà†Èô§ÔºöÂ≠òÂú®‰∏ãÂ±ûËÆæÂ§áÊó∂‰ºöÊãíÁªùÔºâÔºü')) {
      context.grafana.locationService.partial({'var-row_delete_plant_id': '', 'var-edit_plant_id': ''}, true);
      return;
    }
    context.grafana.locationService.partial({'var-row_delete_plant_id': ''}, true);
    const dsName = context.panel.options?.update?.datasource || 'TimescaleDB-Admin';
    const dsInfo = await context.grafana.backendService.get('/api/datasources/name/' + encodeURIComponent(dsName));
    const uid = dsInfo?.uid;
    if (!uid) throw new Error('Êú™ÊâæÂà∞Êï∞ÊçÆÊ∫ê: ' + dsName);
    const rawSql = "SELECT * FROM admin_api.delete_plant(${row_delete_plant_id:sqlstring}, false);";
    const body = {
      queries: [{
        refId: 'A',
        datasource: { uid },
        editorMode: 'code',
        format: 'table',
        rawQuery: true,
        rawSql,
        sql: { columns: [{ parameters: [], type: 'function' }], groupBy: [{ property: { type: 'string' }, type: 'groupBy' }], limit: 50 }
      }],
      from: 'now-5m',
      to: 'now'
