      frame.schema.fields.forEach((f, idx) => { row[f.name] = frame.data.values[idx]?.[0]; });
    }
    const objectId = String(row.plant_id ?? autoId).trim();
    const detail = 'Á∫ßËÅîÁÇπ‰Ωç ' + Number(row.deleted_point_count ?? 0) + 'ÔºåÁ∫ßËÅîËÆæÂ§á ' + Number(row.deleted_device_count ?? 0);
    const nowIso = new Date().toISOString();
    context.grafana.notifySuccess(['Âà†Èô§ÊàêÂäü', 'üóë Â∑≤Âà†Èô§ÂéÇÁ´ô ' + objectId + 'Ôºå' + detail]);
    context.grafana.locationService.partial({'var-row_delete_plant_id': '', 'var-edit_plant_id': '', 'var-last_plant_action': 'üóë Â∑≤Âà†Èô§ÂéÇÁ´ô ' + objectId + 'Ôºå' + detail, 'var-last_plant_action_ts': nowIso}, true);
  try {
  if (typeof context.grafana.refresh === 'function') {
    context.grafana.refresh();
  }
} catch (e) {}

  } catch (error) {
    context.grafana.locationService.partial({'var-row_delete_plant_id': '', 'var-edit_plant_id': ''}, true);
    const msg = error?.message || error?.data?.message || error?.data?.results?.A?.error || error?.statusText || (typeof error === 'string' ? error : '');
    context.grafana.notifyError(['Âà†Èô§Â§±Ë¥•', msg || 'ËØ∑Ê£ÄÊü•ÂÖ≥ËÅîÂÖ≥Á≥ªÊàñËæìÂÖ•']);
  } finally {
