options.elementValueChanged:
if (context.element.id === 'point_pick') {
  const v = String(context.element.value ?? '').trim();
  context.grafana.locationService.partial({'var-edit_point_id': v}, true);
  const syncObj = {};
  syncObj['point_pick'] = v;
  context.panel.setFormValue(syncObj);
  if (!v) {
    context.panel.setFormValue({ point_id: '', point_name: '', point_type: 'inlet' });
  }
  const focusPrimaryField = () => {
    if (typeof document === 'undefined') return;
    const selectors = [
      'input[name="point_id"]',
      'textarea[name="point_id"]',
      'input[id="point_id"]',
      '[aria-label="point_id"]',
      '[data-testid*="point_id"] input',
    ];
    for (const selector of selectors) {
      const el = document.querySelector(selector);
      if (el && typeof el.focus === 'function') {
        el.focus();
        if (typeof el.select === 'function') {
          el.select();
        }
        break;
      }
    }
  };
  setTimeout(focusPrimaryField, 0);
  setTimeout(focusPrimaryField, 120);
}

