const autoId = String('${row_delete_point_id}' ?? '').trim();
if (!autoId) return;
const guardStore = (typeof window !== 'undefined')
  ? (window.__iotAdminDeleteGuards = window.__iotAdminDeleteGuards || {})
  : null;
const guardKey = 'row_delete_point_id:' + autoId;
if (guardStore && guardStore[guardKey]) return;
if (guardStore) {
  guardStore[guardKey] = true;
}
const releaseGuard = () => {
  if (guardStore) {
    delete guardStore[guardKey];
  }
};
return (async () => {
  try {
    if (!confirm('Á°ÆËÆ§Âà†Èô§ÁÇπ‰Ωç ' + autoId + 'ÔºàÈùûÂº∫Âà∂Âà†Èô§ÔºöÂ≠òÂú®‰∏ãÂ±ûËÆæÂ§áÊó∂‰ºöÊãíÁªùÔºâÔºü')) {
      context.grafana.locationService.partial({'var-row_delete_point_id': '', 'var-edit_point_id': ''}, true);
      return;
    }
    context.grafana.locationService.partial({'var-row_delete_point_id': ''}, true);
    const dsName = context.panel.options?.update?.datasource || 'TimescaleDB-Admin';
    const dsInfo = await context.grafana.backendService.get('/api/datasources/name/' + encodeURIComponent(dsName));
    const uid = dsInfo?.uid;
    if (!uid) throw new Error('Êú™ÊâæÂà∞Êï∞ÊçÆÊ∫ê: ' + dsName);
    const rawSql = "SELECT * FROM admin_api.delete_point(${row_delete_point_id:sqlstring}, false);";
    const body = {
      queries: [{
        refId: 'A',
        datasource: { uid },
        editorMode: 'code',
        format: 'table',
        rawQuery: true,
        rawSql,
        sql: { columns: [{ parameters: [], type: 'function' }], groupBy: [{ property: { type: 'string' }, type: 'groupBy' }], limit: 50 }
      }],
      from: 'now-5m',
      to: 'now'
    };
    const queryResp = await context.grafana.backendService.post('/api/ds/query', body);
    const result = queryResp?.results?.A;
    if (result?.error) throw new Error(result.error);
    const frame = result?.frames?.[0];
    const row = {};
    if (frame?.schema?.fields && Array.isArray(frame?.data?.values)) {
      frame.schema.fields.forEach((f, idx) => { row[f.name] = frame.data.values[idx]?.[0]; });
    }
    const objectId = String(row.point_id ?? autoId).trim();
    const detail = 'Á∫ßËÅîËÆæÂ§á ' + Number(row.deleted_device_count ?? 0);
    const nowIso = new Date().toISOString();
    context.grafana.notifySuccess(['Âà†Èô§ÊàêÂäü', 'üóë Â∑≤Âà†Èô§ÁÇπ‰Ωç ' + objectId + 'Ôºå' + detail]);
    context.grafana.locationService.partial({'var-row_delete_point_id': '', 'var-edit_point_id': '', 'var-last_point_action': 'üóë Â∑≤Âà†Èô§ÁÇπ‰Ωç ' + objectId + 'Ôºå' + detail, 'var-last_point_action_ts': nowIso}, true);
  try {
  if (typeof context.grafana.refresh === 'function') {
    context.grafana.refresh();
  }
} catch (e) {}

  } catch (error) {
    context.grafana.locationService.partial({'var-row_delete_point_id': '', 'var-edit_point_id': ''}, true);
    const msg = error?.message || error?.data?.message || error?.data?.results?.A?.error || error?.statusText || (typeof error === 'string' ? error : '');
    context.grafana.notifyError(['Âà†Èô§Â§±Ë¥•', msg || 'ËØ∑Ê£ÄÊü•ÂÖ≥ËÅîÂÖ≥Á≥ªÊàñËæìÂÖ•']);
  } finally {
    releaseGuard();
  }
})();

